import { useState } from "react"
import {
  FaBars,
  FaSearch,
  FaMicrophone,
  FaUser,
  FaShoppingCart,
} from "react-icons/fa"
import "./Navbar.css"

export default function Navbar({
  wishlist = [],
  cartCount = 0,
  onSearch,
}) {
  const [activeTab, setActiveTab] = useState("MEN")
  const [search, setSearch] = useState("")
  const [menuOpen, setMenuOpen] = useState(false)
  const [wishlistOpen, setWishlistOpen] = useState(false)

  const handleSearch = (value) => {
    setSearch(value)
    onSearch(value)
  }

  return (
    <>
      {/* MOBILE MENU */}
      <div className={`side-menu ${menuOpen ? "open" : ""}`}>
        {["MEN", "WOMEN", "SNEAKERS", "WISHLIST"].map((item) => (
          <p key={item} onClick={() => setMenuOpen(false)}>
            {item}
          </p>
        ))}
      </div>

      <header className="navbar">
        {/* LEFT */}
        <div className="nav-left">
          <FaBars
            className="menu-icon mobile-only"
            onClick={() => setMenuOpen(!menuOpen)}
          />

          <nav className="nav-links desktop-only">
            {["MEN", "WOMEN", "SNEAKERS"].map((tab) => (
              <span
                key={tab}
                className={activeTab === tab ? "active" : ""}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </span>
            ))}

            <span
              className={wishlistOpen ? "active" : ""}
              onClick={() => setWishlistOpen(!wishlistOpen)}
            >
              WISHLIST ({wishlist.length})
            </span>
          </nav>
        </div>

        {/* CENTER */}
        <div className="nav-center">
          <h2 className="logo">The Souled Store</h2>
        </div>

        {/* RIGHT */}
        <div className="nav-right">
          {/* SEARCH */}
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search for products..."
              value={search}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <FaMicrophone className="mic-icon" />
            <FaSearch className="search-icon" />
          </div>

          {/* PROFILE */}
          <FaUser className="nav-icon" />

          {/* CART */}
          <div className="icon-badge">
            <FaShoppingCart className="nav-icon" />
            {cartCount > 0 && <span className="badge">{cartCount}</span>}
          </div>
        </div>
      </header>

      {/* WISHLIST DROPDOWN */}
      {wishlistOpen && (
        <div className="wishlist-dropdown">
          <h4>My Wishlist</h4>

          {wishlist.length === 0 && (
            <p className="empty-text">No items wishlisted</p>
          )}

          {wishlist.map((item) => (
            <div key={item.id} className="wishlist-item">
              <img src={item.image} alt={item.name} />
              <div>
                <p>{item.name}</p>
                <span>₹{item.price}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  )
}
