import { useState } from "react"
import { Link, NavLink } from "react-router-dom"
import {
  FaBars,
  FaSearch,
  FaMicrophone,
  FaUser,
  FaShoppingCart,
} from "react-icons/fa"
import "./Navbar.css"

export default function Navbar({
  wishlist = [],
  cartCount = 0,
  onSearch,
}) {
  const [search, setSearch] = useState("")
  const [menuOpen, setMenuOpen] = useState(false)
  const [wishlistOpen, setWishlistOpen] = useState(false)

  const handleSearch = (value) => {
    setSearch(value)
    onSearch(value)
  }

  return (
    <>
      {/* MOBILE MENU */}
      <div className={`side-menu ${menuOpen ? "open" : ""}`}>
        <NavLink to="/" onClick={() => setMenuOpen(false)}>Home</NavLink>
        <NavLink to="/shop" onClick={() => setMenuOpen(false)}>Shop</NavLink>
        <NavLink to="/wishlist" onClick={() => setMenuOpen(false)}>Wishlist</NavLink>
        <NavLink to="/cart" onClick={() => setMenuOpen(false)}>Cart</NavLink>
        <NavLink to="/about" onClick={() => setMenuOpen(false)}>About</NavLink>
      </div>

      <header className="navbar">
        {/* LEFT */}
        <div className="nav-left">
          <FaBars
            className="menu-icon mobile-only"
            onClick={() => setMenuOpen(!menuOpen)}
          />

          {/* DESKTOP LINKS */}
          <nav className="nav-links desktop-only">
            <NavLink to="/">Home</NavLink>
            <NavLink to="/shop">Shop</NavLink>

            <span
              className="wishlist-link"
              onClick={() => setWishlistOpen(!wishlistOpen)}
            >
              Wishlist ({wishlist.length})
            </span>

            <NavLink to="/cart">Cart ({cartCount})</NavLink>
            <NavLink to="/about">About</NavLink>
          </nav>
        </div>

        {/* CENTER */}
        <div className="nav-center">
          <Link to="/" className="logo">The Souled Store</Link>
        </div>

        {/* RIGHT */}
        <div className="nav-right">
          {/* SEARCH */}
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search for products..."
              value={search}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <FaMicrophone className="mic-icon" />
            <FaSearch className="search-icon" />
          </div>

          {/* PROFILE */}
          <FaUser className="nav-icon" />

          {/* CART ICON ONLY */}
          <div className="icon-badge">
            <FaShoppingCart className="nav-icon" />
            {cartCount > 0 && <span className="badge">{cartCount}</span>}
          </div>
        </div>
      </header>

      {/* WISHLIST DROPDOWN */}
      {wishlistOpen && (
        <div className="wishlist-dropdown">
          <h4>My Wishlist</h4>

          {wishlist.length === 0 && (
            <p className="empty-text">No items wishlisted</p>
          )}

          {wishlist.map((item) => (
            <div key={item.id} className="wishlist-item">
              <img src={item.image} alt={item.name} />
              <div>
                <p>{item.name}</p>
                <span>₹{item.price}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  )
}
