import { Link } from "react-router-dom"
import { useState } from "react";
import {
  FaBars,
  FaSearch,
  FaMicrophone,
  FaUser,
  FaHeart,
  FaShoppingCart,
} from "react-icons/fa";
import "../components/NavBar.css";

export default function Navbar() {
  const [activeTab, setActiveTab] = useState("MEN");
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [wishlistCount, setWishlistCount] = useState(1);
  const [cartCount, setCartCount] = useState(0);

  const handleSearch = () => {
    if (!search.trim()) return;
    alert(`Searching for: ${search}`);
  };

  return (
    <>
      {/* MOBILE MENU */}
      {menuOpen && (
        <div className="side-menu">
          <p onClick={() => setActiveTab("MEN")}>MEN</p>
          <p onClick={() => setActiveTab("WOMEN")}>WOMEN</p>
          <p onClick={() => setActiveTab("SNEAKERS")}>SNEAKERS</p>
        </div>
      )}

      <header className="navbar">
        {/* LEFT */}
        <div className="nav-left">
          <FaBars
            className="menu-icon"
            onClick={() => setMenuOpen(!menuOpen)}
          />

          <nav className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/shop">Shop</Link>
            <Link to="/cart">Cart ({cartCount})</Link>
            <Link to="/about">About</Link>
          </nav>
        </div>

        {/* CENTER */}
        <div className="nav-center">
          <h2 className="logo">The Souled Store</h2>
        </div>

        {/* RIGHT */}
        <div className="nav-right">
          {/* SEARCH */}
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search for products..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSearch()}
            />
            <FaMicrophone className="mic-icon" />
            <FaSearch className="search-icon" onClick={handleSearch} />
          </div>

          {/* PROFILE */}
          <FaUser
            className="nav-icon"
            onClick={() => alert("Go to Login / Profile")}
          />

          {/* WISHLIST */}
          <div className="icon-badge">
            <FaHeart
              className="nav-icon"
              onClick={() => setWishlistCount(wishlistCount + 1)}
            />
            {wishlistCount > 0 && (
              <span className="badge">{wishlistCount}</span>
            )}
          </div>

          {/* CART */}
          <div className="icon-badge">
            <FaShoppingCart
              className="nav-icon"
              onClick={() => setCartCount(cartCount + 1)}
            />
            {cartCount > 0 && (
              <span className="badge">{cartCount}</span>
            )}
          </div>
        </div>
      </header>
    </>
  );
}
