import { Link, useNavigate } from "react-router-dom"
import { useState, useRef, useEffect } from "react"
import {
  FaBars,
  FaSearch,
  FaHeart,
  FaShoppingCart,
  FaUser,
} from "react-icons/fa"
import "../components/NavBar.css"

export default function Navbar({
  cartCount = 0,
  wishlistCount = 0,
  onSearch,
}) {
  const [search, setSearch] = useState("")
  const [menuOpen, setMenuOpen] = useState(false)
  const [userMenuOpen, setUserMenuOpen] = useState(false)
  const navigate = useNavigate()
  const userRef = useRef(null)

  const handleSearchChange = (e) => {
    const value = e.target.value
    setSearch(value)
    onSearch(value)

    if (window.location.pathname !== "/shop") {
      navigate("/shop")
    }
  }

  // Close login dropdown when clicking outside
  useEffect(() => {
    const handler = (e) => {
      if (userRef.current && !userRef.current.contains(e.target)) {
        setUserMenuOpen(false)
      }
    }
    document.addEventListener("mousedown", handler)
    return () => document.removeEventListener("mousedown", handler)
  }, [])

  return (
    <>
      {/* MAIN DROPDOWN (HAMBURGER) */}
      {menuOpen && (
        <div className="dropdown-menu">
          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/shop" onClick={() => setMenuOpen(false)}>Shop</Link>
          <Link to="/wishlist" onClick={() => setMenuOpen(false)}>Wishlist</Link>
          <Link to="/cart" onClick={() => setMenuOpen(false)}>Cart</Link>
          <Link to="/about" onClick={() => setMenuOpen(false)}>About</Link>
        </div>
      )}

      <header className="navbar">
        {/* LEFT */}
        <div className="nav-left">
          <FaBars
            className="menu-icon"
            onClick={() => setMenuOpen(!menuOpen)}
          />

          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/shop">Shop</Link>
            <Link to="/about">About</Link>
          </div>
        </div>

        {/* CENTER */}
        <div className="nav-center">
          <Link to="/" className="logo">E-Commerce</Link>
        </div>

        {/* RIGHT */}
        <div className="nav-right">
          {/* SEARCH */}
          <div className="search-box">
            <input
              type="text"
              placeholder="Search products..."
              value={search}
              onChange={handleSearchChange}
            />
            <FaSearch />
          </div>

          {/* WISHLIST */}
          <Link to="/wishlist" className="icon-badge">
            <FaHeart />
            {wishlistCount > 0 && <span className="badge">{wishlistCount}</span>}
          </Link>

          {/* CART */}
          <Link to="/cart" className="icon-badge">
            <FaShoppingCart />
            {cartCount > 0 && <span className="badge">{cartCount}</span>}
          </Link>

          {/* LOGIN DROPDOWN */}
          <div className="user-dropdown" ref={userRef}>
            <FaUser
              className="icon-btn"
              onClick={() => setUserMenuOpen(!userMenuOpen)}
            />

            {userMenuOpen && (
              <div className="user-menu">
                <Link to="/login" onClick={() => setUserMenuOpen(false)}>
                  Login
                </Link>
                <Link to="/signup" onClick={() => setUserMenuOpen(false)}>
                  Sign Up
                </Link>
              </div>
            )}
          </div>
        </div>
      </header>
    </>
  )
}
